const systemStartTime = BigInt(Date.now());
const systemStartHRNanoSec = process.hrtime.bigint();
const systemStartOriginNanoSec = systemStartTime * BigInt(1000000) - systemStartHRNanoSec;

/**
 * @returns current time stamp in nano seconds.
 */
module.exports.getNanoSecTimeStamp = function() {
  const nowNanoSec = process.hrtime.bigint();  // this is system clock time and not posix time
  return systemStartOriginNanoSec + nowNanoSec;
};
