var chai = require('chai');
var expect = chai.expect;
var assert = chai.assert;
chai.config.includeStack = true;

var os = require('os');
var availMem = require('../libs/index').availableMemory;

describe('availableMemory', function() {
  it('should bigger than os.freemem', function(done) {
    availMem(function(err, avail) {
      var freemem = os.freemem();
      expect(avail).to.be.above(freemem);
      done();
    });
  });
});

