var chai = require('chai');
var expect = chai.expect;
chai.config.includeStack = true;

const getNanoSecTimeStamp = require('../libs/get-nano-sec-time-stamp.js').getNanoSecTimeStamp;

describe('getNanoSecTimestamp', function() {
  it('should give monotonically increasing timestamp', function() {
    let timestamp = getNanoSecTimeStamp();
    for (let i = 0; i < 1000; ++i) {
      const next = getNanoSecTimeStamp();
      expect(timestamp < next).to.be.true;  // eslint-disable-line chai-expect/no-inner-compare
      timestamp = next;
    }
  });
});
